# Quel est le handle le plus court de cette liste ? 

def short(handle)
  plusPetit = handle.min_by{|a| a.size}
  puts "Le plus court est : #{plusPetit}"
  gets.chomp
end



# Combien y-a-t'il de handle contenant 5 caractères
# (le @ ne compte pas pour 1 caractère)

def handleNumberFive (list)
    number=0
    list.each do|i|
        if i.slice!(0)=="@" #slice! va supprimer les @
            i=i.slice (1, i.length)
        end
        if i.length== 5
            number +=1
        end
    end
    puts"Nombre de Handles de 5 caractères => #{number}
    puts "\n"
 end
 
 
